import TileCard from "./TileCard";
import Button from "./Button";
import "./TileArea.css";
import "./Suggestions.css";

function TileArea(props) {
  // Gets array variable from Button.js and passes it to App.js
  function getDivHandler(cardFromButton) {
    console.log("TileArea.js");
    props.getToApp(cardFromButton);
  }

  //Gets suggestionsComp variable (that holds component for suggestions)
  // function getSuggHandler(suggParameter) {
  //   return suggParameter;
  // }

  // declares variable to be used in if statement
  let suggestionsComp = undefined;

  // if statement for if suggestions should appear
  // GET THIS TO WORK!!!
  if (props.item.task > [0]) {
    suggestionsComp = (
      <div className="parent-suggestions">
        <h2>Some Tax Suggestions</h2>
        <ol className="suggestions">
          <li>
            1. Give Gifts. One way to get around the estate tax is to hand off
            portions of your wealth to your family members through gifts
          </li>
          <li>2. Set up an Irrevocable Life Insurance Trust</li>
          <li>3. Make Charitable Donations</li>
          <li>4. Establish a Family Limited Partnership</li>
          <li>5. Fund a Qualified Personal Residence Trust</li>
        </ol>
      </div>
    );
  } else {
    suggestionsComp = undefined;
  }

  return (
    <div>
      <Button getDiv={getDivHandler} />
      <div className="tile">
        {props.item.map((mapArray) => (
          <TileCard task={mapArray.task} description={mapArray.description} />
        ))}
      </div>
      {suggestionsComp}
    </div>
  );
}

export default TileArea;
