function Suggestions() {
  return (
    <div>
      <h2>5 Ways to Avoid Paying Estate Tax</h2>
      <ul>
        <li>
          Give Gifts. One way to get around the estate tax is to hand off
          portions of your wealth to your family members through gifts
        </li>
        <li>Set up an Irrevocable Life Insurance Trust</li>
        <li>Make Charitable Donations</li>
        <li>Establish a Family Limited Partnership</li>
        <li>Fund a Qualified Personal Residence Trust</li>
      </ul>
    </div>
  );
}

export default Suggestions;
