import "./UserJob.css";

function UserJob(props) {
  // if statement for paragraph that appears below tax number owed.
  let taxesText = "";
  if (props.description > 11.7) {
    taxesText = "Million owed in estate taxes";
  } else {
    taxesText = "No estate taxes owed";
  }

  return (
    <div>
      <p className="num-owed">{props.description}</p>
      <p>${taxesText}</p>
    </div>
  );
}

export default UserJob;
