import "./Suggestions.css";

function Suggestions() {
  return (
    <div>
      <h2>Some Tax Suggestions</h2>
      <ol className="parent-suggestions">
        <li>
          1. Give Gifts. One way to get around the estate tax is to hand off{" "}
          <br></br>
          portions of your wealth to your family members through gifts
        </li>
        <li>2. Set up an Irrevocable Life Insurance Trust</li>
        <li>3. Make Charitable Donations</li>
        <li>4. Establish a Family Limited Partnership</li>
        <li>5. Fund a Qualified Personal Residence Trust</li>
      </ol>
    </div>
  );
}

export default Suggestions;
