import "./Graph.css";

function Graph(props) {
  //This is the calculation for the fill-bar graph %
  let barFillWidth = props.description;
  barFillWidth = Math.round((11.7 / barFillWidth) * 100) + "%";

  //  taxable calculation for legend near the bar chart
  let taxableEstateValue = Math.round(props.description - 11.7);

  return (
    <div className="bar-area">
      <div className="base-bar">
        <div className="fill-bar" style={{ width: barFillWidth }}></div>
      </div>
      <div>
        <div className="label1">Non-taxable: $11.7 million</div>
        <div className="label2">+ Taxable: ${taxableEstateValue} million</div>
        <div className="label3">
          = Total before taxes: ${props.description} million
        </div>
      </div>
    </div>
  );
}

export default Graph;
